import { Select } from 'antd';
import React, { useState } from 'react';

const { withSelectAll } = HOCS;
const StatusSelect = withSelectAll(Select);

const Demo = () => {
  const [status, setStatus] = useState<string[]>(['all']);

  return (
    <div>
      <StatusSelect
        style={{ width: 300 }}
        mode="multiple"
        options={[
          {
            label: '\u5168\u9009',
            value: 'all',
          },
          {
            label: '\u6210\u529F',
            value: 'success',
          },
          {
            label: '\u5931\u8D25',
            value: 'failed',
          },
        ]}
        allOptionValue="all"
        value={status}
        onChange={(val) => {
          setStatus(val as string[]);
        }}
      />
    </div>
  );
};

export default Demo;
